import React from "react"
import Pokecard from "./pokecard"
import Pokeinfo from "./pokeInfo"
import "./pokedeck.css"
const Pokedex = (props) => {
    const total = props.info.reduce((acc, i) => acc + i.base_experience, 0);
    let result = document.getElementsByClassName("result");
    if (props.isWinner === true) {
        result.html = "You Win";
    } else {
        result.text = "You Loose";
    }


    return (
        <div className="deckInfo"> 

        <div className="deck">

                {props.info.map(i => (

                <Pokecard name={i.name} id={i.id} type={i.type} base_experience={i.base_experience}/>
                ))
                }

            </div>
         <p>Total Exp:{total} </p>
            <p className="result"> {props.isWinner} </p>
        </div>
    )

}
export default Pokedex
